newButton :: Int -> Char -> Int
newButton currentButton direction = 
    case direction of
        'D' -> goDown currentButton
        'U' -> goUp currentButton
        'L' -> goLeft currentButton
        'R' -> goRight currentButton
        _ -> error $ printf "Invalid direction: %c" direction
    where
        goDown currentButton
            | elem currentButton [7,8,9] = currentButton
            | otherwise = currentButton + 3
        goUp currentButton
            | elem currentButton [1,2,3] = currentButton
            | otherwise = currentButton - 3
        goLeft currentButton
            | elem currentButton [1,4,7] = currentButton
            | otherwise = currentButton - 1
        goRight currentButton
            | elem currentButton [3,6,9] = currentButton
            | otherwise = currentButton + 1

findButton :: String -> Int 
findButton directionList =
    findButton' directionList 5
    where 
        findButton' (direction:directions) currentButton =
            findButton' directions (newButton currentButton direction)
        findButton' [] currentButton = currentButton


findCode :: [String] -> [Int]
findCode rawInput= 
    map findButton rawInput 
